package com.example.yourday.viewmodel.motivation

import android.app.Application
import androidx.lifecycle.AndroidViewModel
import androidx.lifecycle.viewModelScope
import com.example.yourday.model.LocalDailyQuote
import com.example.yourday.repository.motivation.DailyQuoteRepository
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.launch

class DailyQuoteViewModel(
    application: Application,
    private val repository: DailyQuoteRepository
) : AndroidViewModel(application) {
    val quote: MutableStateFlow<LocalDailyQuote?> = MutableStateFlow(null)

    fun loadQuoteByDate(date: String) {
        viewModelScope.launch {
            repository.getByDate(date).collect { quoteData ->
                quote.value = quoteData
            }
        }
    }

    suspend fun getById(id: Int): LocalDailyQuote? {
        return repository.getById(id)
    }

    fun upsertQuote(quote: LocalDailyQuote) {
        viewModelScope.launch {
            repository.upsert(quote)
        }
    }

    fun deleteQuote(quote: LocalDailyQuote) {
        viewModelScope.launch {
            repository.delete(quote)
        }
    }

